    $(function () {
        var gtt = new CreatGoToTop("jsGoToTop");
        gtt.goToTop();
        /**
         * @description 控制问题显隐
         * @author  myx
         * @date  20170802
         */
        $(window.document).on('click', function (e) {
            var $target = $((e || window.event).target);
            var $q_tr = $(".question_cnt_tr");
            if ($target.hasClass('question_t_txt')) {//问题回复的隐藏和显示
                if ($target.parents("tr").next("tr").hasClass('ch-hide')) {
                    $target.parents("tr").next("tr").removeClass('ch-hide');
                } else {
                    $target.parents("tr").next("tr").addClass('ch-hide');
                }
                //$target.parents("tr").next("tr").toggle();
            } else if ($target.attr('id') == 'allShow') {//全部展开
                $q_tr.removeClass('ch-hide');
                //$q_tr.show();
            } else if ($target.attr('id') == 'allHidden') {//全部收起
                $q_tr.addClass('ch-hide');
                //$q_tr.hide();
            }
        });
        //咨询开放时间详细内容（内容过长需要隐藏显示）
        $('.close_time').parent().css('display', 'inline');
        $('.close_time').html('');
        $('body').on('click', '.more_time', function () {
            var $this = $(this);
            var $ul = $this.parent('span.one_time').next('ul.opentime');
            if ($ul.length != 0) {
                layer.open({
                    type: 1,
                    title: '论坛开放时间', //不显示标题
                    area: ['400px', '310px'], //弹框宽高
                    content: $ul //捕获的元素，注意：最好该指定的元素要存放在body最外层，否则可能被其它的相对元素所影响
                });
            }
        });
    });
